/*==================================================
AllStudentsView.js

The Views component is responsible for rendering web page with data provided by the corresponding Container component.
It constructs a React component to display the all students view page.
================================================== */
import { Link } from "react-router-dom";

const AllStudentsView = ({ students, deleteStudent }) => {
  if (!students.length) {
    return (
      <div style={{ textAlign: 'center', marginTop: '40px' }}>
        <p>No students found.</p>
        <Link to={`/newstudent`}>
          <button style={{ padding: '10px 20px' }}>Add New Student</button>
        </Link>
      </div>
    );
  }

  return (
    <div style={{ textAlign: 'center', padding: '30px' }}>
      <h1>All Students</h1>

      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        gap: '20px',
        marginTop: '30px'
      }}>
        {students.map((student) => (
          <div key={student.id} style={{
            width: '260px',
            backgroundColor: '#f4f6fa',
            borderRadius: '10px',
            padding: '20px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            transition: 'transform 0.2s ease-in-out'
          }}>
            <Link to={`/student/${student.id}`} style={{ textDecoration: 'none', color: '#11153e' }}>
              <h3 style={{ fontFamily: 'Quicksand', fontSize: '22px' }}>
                {student.firstname} {student.lastname}
              </h3>
            </Link>
            <p style={{ fontSize: '15px', color: '#666' }}>Campus ID: {student.campusId || 'N/A'}</p>
            <button
              onClick={() => deleteStudent(student.id)}
              style={{
                backgroundColor: '#c62828',
                color: 'white',
                border: 'none',
                padding: '8px 12px',
                borderRadius: '6px',
                cursor: 'pointer',
                marginTop: '10px'
              }}
            >
              Delete
            </button>
          </div>
        ))}
      </div>

      <Link to={`/newstudent`}>
        <button style={{
          marginTop: '40px',
          padding: '12px 24px',
          fontSize: '16px',
          backgroundColor: '#1976d2',
          color: 'white',
          border: 'none',
          borderRadius: '6px',
          cursor: 'pointer'
        }}>
          Add New Student
        </button>
      </Link>
    </div>
  );
};

export default AllStudentsView;
